mapper.update:
update sms_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phone_number = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_user = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
